            ctx.fillRect(this.x - 5, this.y - 5, 10, 10);
        }
    }

    hit() {
        if (this.visible) {
            this.visible = false;
            score++;
            scoreValue.textContent = score;
            whackSound.play();
            return true;
        }
        return false;
    }
}

