    draw() {
        ctx.fillStyle = this.type === 'hammer' ? 'red' : 'blue';
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size / 2, 0, 2 * Math.PI);
        ctx.fill();
        ctx.fillStyle = 'white';
